using UnityEngine;public class deathreset:MonoBehaviour{public Rigidbody2D rb;public Transform character;public Transform spawnPoint;public Collider2D[] deadlyColliders;Vector3 startPos;void Awake(){if(character==null)character=transform;startPos=(spawnPoint!=null?spawnPoint.position:character.position);}void OnCollisionEnter2D(Collision2D c){Hit(c.collider);}void OnTriggerEnter2D(Collider2D c){Hit(c);}void Hit(Collider2D c){for(int i=0;i<deadlyColliders.Length;i++){if(deadlyColliders[i]==c){Die();return;}}}void Die(){if(rb!=null){rb.linearVelocity=Vector2.zero;rb.angularVelocity=0;rb.simulated=false;}if(character!=null)character.position=startPos;if(rb!=null)rb.simulated=true;}}

